//Write a program that prints the multiplication table from 1 to 5
public class LAB02_Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=5;i++) {
			for(int j=1;j<=10;j++) {
				System.out.println(""+i+" X "+j+" = "+(i*j));
			}System.out.println("-------------");
		}
	}

}
